	{
		if (PhotonNetwork.LocalPlayer != PhotonNetwork.MasterClient)
		{
			NetworkManager.opponentPlayer = player;
			PhotonNetwork.JoinRoom(NetworkManager.PlayerToString(NetworkManager.opponentPlayer));
		}
		else
