	{
		if (!waitingOpponent)
		{
			base.enabled = false;
		}
		waitingProgress += 2f * orient * Time.deltaTime;
		int num = (int)waitingProgress;
		if (waitingProgress > 3.49f || waitingProgress < -0.49f)
		{
			orient *= -1f;
		}
		if (waitingProgressInt != num)
		{
			waitingProgressInt = num;
			string text = "Waiting";
			for (int i = 1; i <= waitingProgressInt; i++)
			{
				text += ".";
