	private void DelayForce5_Head()
	{
		bones[7].ragdoll_rb.velocity = (base.transform.up * 2f + -base.transform.forward * 1f).normalized * 5f;
		bones[11].ragdoll_rb.velocity = base.transform.up * 10f;
