	{
		Init();
	}

	private void Init()
	{
		if (isInit)
		{
			return;
		}
		Vector3 size = base.transform.GetComponent<BoxCollider>().size;
		if (Mathf.Abs(size.x - size.y) >= 0.001f)
		{
			if (size.x - size.y < 0f)
			{
				buttonRatio = ButtonRatio.Height;
				halfExtents = size.x * 0.5f;
				ray_dist = (size.y - size.x) * 0.5f;
