				resultTexts[0].sprite = sprite_resultText_eng[0];
			}
			resultTexts[0].gameObject.SetActive(value: true);
			resultTexts[1].gameObject.SetActive(value: false);
			resultTexts[2].gameObject.SetActive(value: false);
			if (!isResultUpload)
