	public static AICatcher GetInstance()
	{
		return instance;
	}

	private void Start()
	{
		instance = this;
		gCatcherIK = GetComponent<CatcherIK>();
		gCatcherIK.StartSet();
		gCatcherIK.setTargetPos(pCatcherPoint.transform.position);
		PrePos = base.transform.position;
