		private float drag = 1.6f;

		private float time;

		private TrailRenderer trail;

		private GameObject effect_Smash;

		public AudioSource audioSource;

		public AudioClip[] audioSounds;

		public Transform trans { get; set; }

		public Rigidbody rigid { get; set; }

