		{
			switch (tag)
			{
			case "Catcher":
				if (BallMove.GetInstance().hit)
				{
					break;
				}
				BallMove.GetInstance().BallStop();
				BallMove.GetInstance().AddForceCorouStop();
				Call_PlaySound(SoundTrack.Tack);
				BallMove.GetInstance().BallDestroy(1.3f);
