					UnityEngine.Object.Destroy(UnityEngine.Object.Instantiate(gParticle.ParticleArray[4], BallMove.GetInstance().transform.position, Quaternion.identity), 0.75f);
					Call_PlaySound(SoundTrack.Strike_1);
