		private void Update()
		{
			if (Input.GetKeyDown(KeyCode.A))
			{
				StartCoroutine(TestThrow());
			}
			bool flag = false;
			if (SelectParent.gameObject.activeSelf)
			{
				for (int i = 0; i < 5; i++)
				{
					if ((BallImage.transform.position - SelectBall[i].position).magnitude < 0.4f && SelectBall[i].gameObject.activeSelf)
					{
