		{
			StartCoroutine(HitterRunAniCheck());
		}

		private IEnumerator HitterRunAniCheck()
		{
			yield return null;
			gAnimator.SetTrigger("Running");
			yield return null;
			gAnimator.SetTrigger("Running");
		}
