					player[i].gameScoreImage[1].sprite = getInstance.sprites[13];
					break;
				case 2:
					player[i].gameScoreImage[0].sprite = getInstance.sprites[14];
					player[i].gameScoreImage[1].sprite = getInstance.sprites[14];
					break;
				}
