		{
			ParticleSystem particleSystem = targetPool.First((ParticleSystem system) => !system.isPlaying);
			particleSystem.transform.position = pos;
			particleSystem.transform.LookAt(particleSystem.transform.position + lookDir);
			particleSystem.Play();
		}
