		private void Awake()
		{
			RunRoutineWrapper = CoroutineWrapper.Generate(this);
			Billiards.Singleton<BoxDataContainer>.Instance.IsPause.OnDataChanged += IsPause_OnDataChanged;
			for (int i = 0; i < 50; i++)
			{
				Note note = UnityEngine.Object.Instantiate(NoteOrigin);
