			}
			Billiards.Singleton<BoxDataContainer>.Instance.BPM.CurrentData = CurrentLevelData.BPM;
			ScoreAndComboManager instance = Billiards.Singleton<ScoreAndComboManager>.Instance;
			int noteCount = targetData._notes.Count;
