		{
			float num = CutValue(v.x);
			float num2 = CutValue(v.y);
			float num3 = CutValue(v.z);
			if (num == 0f && num2 == 0f && num3 == 0f)
			{
				return "z";
			}
			if (num == 1f && num2 == 0f && num3 == 0f)
			{
				return "r";
			}
			if (num == 0f && num2 == 1f && num3 == 0f)
			{
				return "u";
			}
			if (num == 0f && num2 == 0f && num3 == 1f)
			{
				return "f";
			}
			if (num == -1f && num2 == 0f && num3 == 0f)
			{
				return "l";
			}
			if (num == 0f && num2 == -1f && num3 == 0f)
			{
				return "d";
			}
			if (num == 0f && num2 == 0f && num3 == -1f)
			{
				return "b";
			}
			if (num == 1f && num2 == 1f && num3 == 1f)
			{
				return "o";
			}
			return "(" + num.ToString4() + ", " + num2.ToString4() + ", " + num3.ToString4() + ")";
		}

		public static Vector3 Vector3FromString(string s)
		{
			switch (s)
			{
			case "":
			case "z":
				return Vector3.zero;
			case "r":
				return Vector3.right;
			case "u":
				return Vector3.up;
			case "f":
				return Vector3.forward;
			case "l":
				return Vector3.left;
			case "d":
				return Vector3.down;
			case "b":
				return Vector3.back;
			case "o":
				return Vector3.one;
			default:
			{
				string text = "";
				string text2 = "";
				string text3 = "";
				int num = 1;
				for (int i = 0; i < s.Length; i++)
				{
					char c = s[i];
					switch (c)
					{
					case ',':
						num++;
						continue;
					case ' ':
					case '(':
					case ')':
						continue;
					}
					switch (num)
					{
					case 1:
						text += c;
						break;
					case 2:
						text2 += c;
						break;
					case 3:
						text3 += c;
						break;
					}
				}
				float x = text.ToFloat4();
				float y = text2.ToFloat4();
				float z = text3.ToFloat4();
				return new Vector3(x, y, z);
			}
			}
		}

