		public static string[] ConvertDataToStringArray(string data)
		{
			List<string> list = new List<string>(0);
			string text = "";
			for (int i = 0; i < data.Length; i++)
			{
				char c = data[i];
				switch (c)
				{
				case ']':
					break;
				case ';':
					list.Add(text);
					text = "";
					continue;
				default:
					text += c;
					continue;
				case ' ':
				case '[':
					continue;
				}
				list.Add(text);
				break;
			}
			return list.ToArray();
		}

		public static string[] ConvertArrayDataToStringArray(string data)
		{
			List<string> list = new List<string>(0);
			string text = "";
			for (int i = 0; i < data.Length; i++)
			{
				char c = data[i];
				switch (c)
				{
				case ']':
					text += c;
					list.Add(text);
					text = "";
					break;
				default:
					text += c;
					break;
				case ' ':
					break;
				}
			}
			return list.ToArray();
		}
