					playerProfileUI.SetPlayer(player);
					playerProfileUI.gameObject.SetActive(value: true);
				}
				Pointer component3 = currentButton.GetComponent<Pointer>();
				if ((bool)component3 && UnityEngine.Application.isMobilePlatform)
