			case Define.HitType.Great:
				great++;
				break;
			case Define.HitType.Good:
				good++;
				break;
			}
		}

		public void ResetCombo()
		{
			if (maxCombo < combo)
			{
				maxCombo = combo;
			}
			combo = 0;
		}

		public void Reset()
		{
			score = 0f;
			perfect = 0;
			great = 0;
			good = 0;
			combo = 0;
