		{
			if (controller.inputDevice.TryGetFeatureValue(CommonUsages.trigger, out var value))
			{
				switch (type)
				{
				case ControllerType.Left:
					GloveAnimator.SetFloat("BlendL", value);
					break;
				case ControllerType.Right:
					GloveAnimator.SetFloat("BlendR", value);
					break;
				}
