		private void FixedUpdate()
		{
			if (!PhotonManager.isValid)
			{
				return;
			}
			foreach (TrackedPoseTransform trackedPoseTransform in trackedPoseTransforms)
			{
				float dt = Time.fixedDeltaTime;
				trackedPoseTransform.UpdateTransform(in dt);
