		textMesh.font = Resources.Load<Font>("UI/trebuc2");
		textMesh.anchor = TextAnchor.MiddleCenter;
	}

	protected override void Update()
	{
		base.Update();
		if (fadeTarget != alpha)
		{
			if (fadeTarget < alpha - fadeSpeed)
			{
				alpha -= fadeSpeed;
			}
			else if (fadeTarget > alpha + fadeSpeed)
			{
				alpha += fadeSpeed;
			}
			else
			{
