			remoteUser.Value.RemoteAvatar.VoiceAmplitude = voiceAmplitude;
		}
		if (localAvatar != null)
		{
			localAvatar.VoiceAmplitude = Mathf.Clamp(voiceCurrent * VOIP_SCALE, 0f, 1f);
		}
