			if (array.Length <= 0)
			{
				return;
			}
			switch (array[0])
			{
			case "p":
				if (array.Length > 2)
				{
					createAndJoinPrivateRoom(array[1], array[2]);
				}
				break;
			case "c":
				getCurrentRoom();
				break;
			case "g":
				if (array.Length > 1)
				{
					getRoom(array[1]);
				}
				break;
			case "j":
				if (array.Length > 1)
				{
					joinRoom(array[1]);
				}
				break;
			case "l":
				if (array.Length > 1)
				{
					leaveRoom(array[1]);
				}
				break;
			case "k":
				if (array.Length > 2)
				{
					kickUser(array[1], array[2]);
				}
				break;
			case "m":
				getLoggedInUser();
				break;
			case "u":
				if (array.Length > 1)
				{
					getUser(array[1]);
				}
				break;
			case "d":
				getLoggedInFriends();
				break;
			case "i":
				getInvitableUsers();
				break;
			case "o":
				if (array.Length > 2)
				{
					inviteUser(array[1], array[2]);
				}
				break;
			case "s":
				if (array.Length > 2)
				{
					setRoomDescription(array[1], array[2]);
				}
				break;
			case "w":
				if (array.Length > 3)
				{
					updateRoomDataStore(array[1], array[2], array[3]);
				}
				break;
			case "n":
				getUserNonce();
				break;
			case "e":
				checkEntitlement();
				break;
			case "a":
				if (array.Length > 1)
				{
					getAchievementDefinition(array[1]);
				}
				break;
			case "b":
				if (array.Length > 1)
				{
					getAchievementProgress(array[1]);
				}
				break;
			case "3":
				if (array.Length > 1)
				{
					unlockAchievement(array[1]);
				}
				break;
			case "4":
				if (array.Length > 2)
				{
					addCountAchievement(array[1], array[2]);
				}
				break;
			case "5":
				if (array.Length > 2)
				{
					addFieldsAchievement(array[1], array[2]);
				}
				break;
			case "1":
				if (array.Length > 2)
				{
					writeLeaderboardEntry(array[1], array[2]);
				}
				break;
			case "2":
				if (array.Length > 1)
				{
					getLeaderboardEntries(array[1]);
				}
				break;
			default:
				printOutputLine("Invalid Command");
				break;
			}
		}

