			{
				TerminateWithError(msg);
			}
			else
			{
				Users.GetLoggedInUser().OnComplete(GetLoggedInUserCallback);
			}
		}

		private void GetLoggedInUserCallback(Message<User> msg)
		{
			if (msg.IsError)
			{
				TerminateWithError(msg);
				return;
			}
			m_myID = msg.Data.ID;
