	public Transform Node;

	public float OverrideTime;

	private float m_Time;

	public void Awake()
	{
		m_Time = Time.time;
	}

	private void Update()
	{
		if (Effect == null)
		{
			return;
		}
		if (OverrideTime > 0f)
		{
			if (Time.time >= m_Time + OverrideTime)
			{
				UnityEngine.Object.Instantiate(Effect, Node.position, Node.rotation);
				m_Time = Time.time;
			}
		}
		else if (Time.time >= m_Time + 1.5f)
		{
