		int num = 0;
		Vector3[] array = new Vector3[segmentCount * divs + 1];
		float num2 = 1f / (float)divs;
		ref Vector3 reference = ref array[num];
		reference = GetDrawPosition(0, 0f);
		num++;
		for (int i = 0; i < segmentCount; i++)
