	private void Update()
	{
		switch (State)
		{
		case EState.Hidden:
			UpdateHidden();
			break;
		case EState.Discovered:
			UpdateDiscovered();
			break;
		case EState.Crying:
			UpdateCrying();
			break;
		case EState.StareDown:
			UpdateStareDown();
			break;
		case EState.Scream:
			UpdateScream();
			break;
		case EState.Chase:
			UpdateChase();
			break;
		case EState.ChaseStretchIn:
			UpdateChaseStretchIn();
			break;
		case EState.ChaseStretchOut:
			UpdateChaseStretchOut();
			break;
		case EState.Attack:
			UpdateAttack();
			break;
		case EState.VisionDisappear:
			UpdateVisionDisappear();
			break;
		case EState.VisionScare:
			UpdateVisionScare();
