	private void WalkToLight_Update()
	{
		if (!cVisibility.CheckVisibilize())
		{
			base.State = States.HiddenPatrol;
			return;
		}
		SetTensionIfVisible();
		if (Vector3Ext.GroundVector(base.transform.position, targetLight.transform.position).sqrMagnitude <= 0.25f)
