	private void EatLight_Update()
	{
		SetTensionIfVisible();
		animator.SetBool("EatLight", value: true);
		timer -= Time.deltaTime;
		if (timer <= 0f)
		{
			base.State = States.Sniff;
			SetParticles(enabled: false);
			return;
