	{
		nodeRangeSq = nodeRange * nodeRange;
		if (backwardsNodeRange <= 0f)
		{
			backwardsNodeRange = nodeRange;
		}
		backwardsNodeRangeSq = backwardsNodeRange * backwardsNodeRange;
		path = new List<Vector3>();
