			virtualMask = OVRInput.Axis2D.SecondaryThumbstick;
			break;
		}
		return OVRInput.Get(virtualMask, (!UsingHandControllers) ? OVRInput.Controller.Gamepad : OVRInput.Controller.Touch);
	}
