		Room room = Map.Get().At(obj.transform.position).Room;
		if (room != null && (room.Template.size == RoomTemplate.SizeType.Hub || obj.GetComponent<Light>().range >= 3f) && obj.GetComponent<RoomAmbientLight>() == null)
