		Vector3 position2 = new Vector3(num3 * num2, (0f - num4) * num2, num2);
		Vector3 position3 = new Vector3((0f - num3) * num2, num4 * num2, num2);
		Vector3 position4 = new Vector3((0f - num3) * num2, (0f - num4) * num2, num2);
