		{
			ScrollWebView();
		}
		if (isAlphaAdjustable)
		{
			ChangeAlpha();
		}
		if (isColorAdjustable)
		{
			ChangeColor();
		}
		if (VRInput.bInputTriggerUp(VRInput.eController.mainhand))
		{
			if (isPositionAdjustableR)
			{
				isPositionAdjustableR = false;
			}
			if (isPositionAdjustableL)
