				}
				else if (dataManager.userData.isMediaOn)
				{
					TurnOffMedia();
					TurnOffUpperPanel();
					Singleton<Console>.Instance.buttonCheckMedia();
				}
			}
			break;
		case SceneState.STAGE:
			if (VRInput.GetScreenButton() && main.tutoControl.menu)
			{
				ClickMediaButton();
			}
			break;
		}
