	private void ChangeMediaColors(int _mediaState)
	{
		for (int i = 0; i < mediaButtonImages.Length; i++)
		{
			switch (_mediaState)
			{
			case 0:
				mediaButtonImages[i].color = unselectedColor;
				break;
			case 1:
				if (i == 0)
				{
					mediaButtonImages[i].color = selectedColor;
				}
				else
				{
					mediaButtonImages[i].color = unselectedColor;
				}
				break;
			case 2:
				if (i == 1)
				{
					mediaButtonImages[i].color = selectedColor;
				}
				else
				{
					mediaButtonImages[i].color = unselectedColor;
				}
				break;
			case 3:
				if (i == 2)
				{
					mediaButtonImages[i].color = selectedColor;
				}
				else
				{
					mediaButtonImages[i].color = unselectedColor;
				}
				break;
			}
		}
	}

