			{
				hookTime += Time.deltaTime;
				if (hookTime > biteWaitTime)
				{
					loop = false;
				}
			}
			if (attractedFish != null)
			{
				if (attractedFish.bEnableHookset && main.tutoControl.hookset && checkControllerSwing(0, hookMinSpeed) == eInputSwing.succeed)
