	private char GetCompleteChar()
	{
		int num = 0;
		num = ((final >= 0) ? final : 0);
		byte[] bytes = BitConverter.GetBytes((short)(initial * 588 + medial * 28 + num + 44032));
		return char.Parse(Encoding.Unicode.GetString(bytes));
	}

	private char Filter(char ch)
	{
