				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
			}
			else if (state == State.Medial && medialFirst.Equals('.'))
			{
				state = State.Initial;
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
			}
			else if (medialPossible && medial != 8 && medial != 13 && medial != 18 && finalFirst.Equals('.') && finalLast.Equals('.'))
			{
				state = State.Medial;
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				medial = CheckMedial(medialFirst.ToString());
				final = -1;
				source += GetCompleteChar();
				medialPossible = true;
			}
			else if ((state == State.Final || state == State.Medial) && !medialFirst.Equals('.') && finalFirst.Equals('.') && finalLast.Equals('.'))
			{
				state = State.Medial;
				medialFirst = '.';
				medialPossible = false;
				medial = -1;
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				source += GetSingleJa(initial);
			}
			else if (state == State.Final && !finalFirst.Equals('.') && !finalLast.Equals('.'))
			{
				state = State.Final;
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				finalLast = '.';
				final = CheckFinal(finalFirst.ToString());
				source += GetCompleteChar();
				finalPossible = true;
			}
			else if (state == State.Final && !finalFirst.Equals('.'))
			{
				int num2 = CheckMedial(medialFirst.ToString());
				if (num2 == 8 || num2 == 13 || num2 == 18)
				{
					medialPossible = true;
					state = State.Medial;
				}
				else
				{
					state = State.Final;
				}
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				final = -1;
				finalFirst = '.';
				source += GetCompleteChar();
			}
			return;
		default:
			if (num < 65 || num > 90)
			{
				initial = -1;
				medial = -1;
				final = -1;
				medialFirst = '.';
				finalFirst = '.';
				finalLast = '.';
				state = State.Initial;
				source += ch;
				return;
			}
			break;
		case 97:
		case 98:
		case 99:
		case 100:
		case 101:
		case 102:
		case 103:
		case 104:
		case 105:
		case 106:
		case 107:
		case 108:
		case 109:
		case 110:
		case 111:
		case 112:
		case 113:
		case 114:
		case 115:
		case 116:
		case 117:
		case 118:
		case 119:
		case 120:
		case 121:
		case 122:
			break;
		}
		if (state == State.Initial)
		{
			initial = CheckInitial(ch);
			if (initial >= 0)
			{
				state = State.Medial;
				source += GetSingleJa(initial);
			}
			else
			{
				state = State.Medial;
				Input(ref source, ch);
			}
		}
		else if (state == State.Medial)
		{
			if (medial < 0)
			{
				medial = CheckMedial(ch.ToString());
				if (medial < 0)
				{
					state = State.Initial;
					Input(ref source, ch);
					return;
				}
				if (initial < 0)
				{
					source += GetSingleMo(CheckMedial(ch.ToString()));
					state = State.Initial;
					medial = -1;
					return;
				}
				if (medial == 8 || medial == 13 || medial == 18)
				{
					medialPossible = true;
					state = State.Medial;
				}
				else
				{
					state = State.Final;
				}
				medialFirst = ch;
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				source += GetCompleteChar();
				return;
			}
			string empty = string.Empty;
			empty += medialFirst;
			empty += ch;
			int num3 = CheckMedial(empty);
			if (num3 > 0)
			{
				medial = num3;
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				source += GetCompleteChar();
				state = State.Final;
			}
			else
			{
				state = State.Final;
				Input(ref source, ch);
			}
		}
		else
		{
			if (state != State.Final)
			{
				return;
			}
			if (final < 0)
			{
				final = CheckFinal(ch.ToString());
				if (final > 0)
				{
					if (source.Length > 0)
					{
						source = source.Substring(0, source.Length - 1);
					}
					source += GetCompleteChar();
					finalFirst = ch;
					if (final == 1 || final == 4 || final == 8 || final == 17)
					{
						finalPossible = true;
					}
				}
				else if (CheckMedial(ch.ToString()) >= 0)
				{
					state = State.Medial;
					initial = -1;
					medial = -1;
					Input(ref source, ch);
				}
				else if (CheckInitial(ch) >= 0)
				{
					finalPossible = false;
					final = 0;
					Input(ref source, ch);
				}
				return;
			}
			if (finalPossible)
			{
				finalPossible = false;
				string empty2 = string.Empty;
				empty2 += finalFirst;
				empty2 += ch;
				int num4 = CheckFinal(empty2);
				if (num4 > 0)
				{
					finalLast = ch;
					final = num4;
					if (source.Length > 0)
					{
						source = source.Substring(0, source.Length - 1);
					}
					source += GetCompleteChar();
				}
				else
				{
					Input(ref source, ch);
				}
				return;
			}
			if (CheckInitial(ch) >= 0)
			{
				finalFirst = '.';
				finalLast = '.';
				state = State.Initial;
				medial = -1;
				final = -1;
				medialFirst = '.';
				medialPossible = false;
				Input(ref source, ch);
				return;
			}
			if (finalLast.Equals('.'))
			{
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				final = 0;
				source += GetCompleteChar();
				initial = CheckInitial(finalFirst);
			}
			else
			{
				if (source.Length > 0)
				{
					source = source.Substring(0, source.Length - 1);
				}
				final = CheckFinal(finalFirst.ToString());
				source += GetCompleteChar();
				initial = CheckInitial(finalLast);
			}
			source += GetSingleJa(initial);
			finalFirst = '.';
			finalLast = '.';
			medialPossible = false;
			medial = -1;
			final = -1;
			state = State.Medial;
			Input(ref source, ch);
		}
	}

	private int CheckInitial(char ch)
	{
		foreach (Initial value in Enum.GetValues(typeof(Initial)))
		{
			if (value.ToString().Equals(ch.ToString()))
			{
				return (int)value;
			}
		}
		return -1;
	}

	private int CheckMedial(string ch)
	{
		foreach (Medial value in Enum.GetValues(typeof(Medial)))
		{
			if (ch.Equals(value.ToString()))
			{
				return (int)value;
			}
		}
		return -1;
	}

	private int CheckFinal(string ch)
	{
		foreach (Final value in Enum.GetValues(typeof(Final)))
		{
			if (ch.Equals(value.ToString()))
			{
				return (int)value;
			}
		}
		return -1;
	}

