		if (photonView != null)
		{
			lure = photonView.gameObject;
		}
	}

	public void RemoveLine()
	{
		lure = null;
	}

	private void Update()
	{
		lineRender.SetPosition(0, base.transform.position);
		if (lure != null)
		{
			lineRender.SetPosition(1, lure.transform.position);
		}
		else
		{
			lineRender.SetPosition(1, base.transform.position);
		}
	}

	public void ChangeAni(FishState fishState)
	{
		switch (fishState)
		{
		case FishState.Swim:
			fishAni.Play("swim");
			UnityEngine.Debug.Log(" animation swim");
			break;
		case FishState.Jump:
			fishAni.SetTrigger("jump");
			UnityEngine.Debug.Log(" animation jump");
			break;
		case FishState.Struggle:
			fishAni.SetBool("struggle", value: true);
			UnityEngine.Debug.Log(" animation struggle");
			break;
		case FishState.Landing:
			fishAni.SetBool("struggle", value: true);
			UnityEngine.Debug.Log(" animation Landing");
			break;
		case FishState.Stay:
		case FishState.Keep:
		case FishState.Release:
		case FishState.Fail:
			break;
		}
	}
