	private void ProcessSpeaking()
	{
		if (morphValues != null)
		{
			if (morphValues[0] < speakingLimit)
			{
				tmp_UserName.color = speakColor;
				tmp_UserName.fontSize = 105f;
			}
			else
			{
				tmp_UserName.color = Color.white;
				tmp_UserName.fontSize = 100f;
			}
		}
		else
		{
			tmp_UserName.color = Color.white;
			tmp_UserName.fontSize = 100f;
