	{
		int num = (int)(time / 60f);
		int num2 = (int)(time % 60f);
		string text = "";
		string text2 = "";
		if (num < 10)
		{
			text += "0";
		}
		if (num2 < 10)
		{
			text2 += "0";
		}
		text += num;
		text2 += num2;
		return $"{text}:{text2}";
	}

