		{
			GameObject obj = UnityEngine.Object.Instantiate(yellowStar, new Vector3(0f, 0f, 0f), base.transform.rotation);
			obj.transform.parent = base.transform;
			obj.transform.localPosition = new Vector3((float)i * distance, 0f, 0f);
