			if (VRInput.bInputTriggerDown(VRInput.Hand.LEFT))
			{
				StartCoroutine(Shot(path));
			}
		}
		else if (phoneState == eHandphoneState.righthand && VRInput.bInputTriggerDown(VRInput.Hand.RIGHT))
		{
