			if (string.IsNullOrEmpty(Category))
			{
				return new List<string>(dictionary.Keys);
			}
			List<string> list = new List<string>();
			for (int i = 0; i < keys.Count; i++)
			{
				KeyData keyData = keys[i];
				if (GetCategoryFromFullKey(keyData.key) == Category)
				{
