		private void Start()
		{
			currentState = states.NOT_INIT;
			localUser = null;
			remoteUser = null;
			currentRoom = null;
			lastPacketID = 0;
			ratedMatchStarted = false;
			Core.Initialize();
			Rooms.SetUpdateNotificationCallback(updateRoom);
			Matchmaking.SetMatchFoundNotificationCallback(foundMatch);
			checkEntitlement();
		}

		private void Update()
		{
			string text = GetComponent<InputField>().text;
			if (Input.GetKey(KeyCode.Return))
			{
				if (text != "")
				{
					SubmitCommand(text);
				}
				GetComponent<InputField>().text = "";
			}
			processNetPackets();
			Request.RunCallbacks();
		}

		private void SubmitCommand(string command)
		{
			string[] array = command.Split('!');
			if (array.Length == 0)
			{
				return;
			}
			switch (array[0])
			{
			case "c":
				requestCreateRoom();
				break;
			case "d":
				requestCreateFilterRoom();
				break;
			case "f":
				requestFindMatch();
				break;
			case "g":
				requestFindRoom();
				break;
			case "i":
				requestFindFilteredRoom();
				break;
			case "s":
				if (array.Length > 1)
				{
					sendChat(array[1]);
				}
				break;
			case "l":
				requestLeaveRoom();
				break;
			case "1":
				requestStartRatedMatch();
				break;
			case "2":
				requestReportResults();
				break;
			default:
				printOutputLine("Invalid Command");
				break;
			}
		}

