		SetKeyword("GREYSCALE_ON");
		SetKeyword("POSTERIZE_ON");
		SetKeyword("BLUR_ON");
		SetKeyword("MOTIONBLUR_ON");
		SetKeyword("GHOST_ON");
		SetKeyword("INNEROUTLINE_ON");
		SetKeyword("HOLOGRAM_ON");
		SetKeyword("CHROMABERR_ON");
