				{
					yield return null;
				}
				if (meshFilter != null)
				{
					meshFilter.mesh = newMesh;
				}
				else if (skin != null)
				{
					skin.sharedMesh = newMesh;
				}
				meshSimplify.m_simplifiedMesh = newMesh;
			}
		}
		m_strLastTitle = "";
		m_strLastMessage = "";
		m_nLastProgress = 0;
	}
