				{
					return FontStyle.BoldAndItalic;
				}
				return original;
			case FontStyle.Italic:
				if (newStyle == FontStyle.Bold)
				{
					return FontStyle.BoldAndItalic;
				}
				return original;
			case FontStyle.BoldAndItalic:
				return original;
